const Navbar = () => {
  const { user } = useUser();
  const pathname = usePathname();

  const navItems = [
    { href: "/dashboard", icon: <DashboardIcon />, label: "Dashboard" },
    {
      href: "/invoice-management",
      icon: <InvoiceManagementIcon />,
      label: "Invoice Management",
    },
    {
      href: "/product-management",
      icon: <ProductManagementIcon />,
      label: "Product Management",
    },
    { href: "/parties", icon: <PartiesIcon />, label: "Parties" },
    { href: "/expenses", icon: <ExpensesIcon />, label: "Expenses" },
    { href: "/reports", icon: <ReportsIcon />, label: "Reports" },
    {
      href: "/bank-accounts",
      icon: <BankAccountsIcon />,
      label: "Bank Accounts",
    },
    {
      href: "/invoice-settings",
      icon: <InvoiceSettingsIcon />,
      label: "Invoice Settings",
    },
    {
      href: "/business-settings",
      icon: <BusinessSettingsIcon />,
      label: "Business Settings",
    },
    { href: "/help-center", icon: <HelpCenterIcon />, label: "Help Center" },
  ];

  return (
    <>
      <nav className="bg-sidebar_white_background text-sidebar_black_text p-4 w-[264px] h-auto border border-sidebar_gray_border">
        <div className="container mx-auto flex flex-col items-start">
          <Link href="/" className="py-4">
            <span className="">
              <InvoicifyIcon />
            </span>
          </Link>
          <div className="flex flex-col gap-[5.2px] w-full ">
            {navItems.map((item) => (
              <Link
                key={item.href}
                href={item.href}
                className={`p-3 flex items-center gap-3 hover:bg-sidebar_green_button_background hover:text-white rounded-lg ${
                  pathname === item.href
                    ? "bg-sidebar_green_button_background text-white"
                    : ""
                }`}
              >
                <span className="">{item.icon}</span> {item.label}
              </Link>
            ))}
          </div>
        </div>
      </nav>
      <div className="w-[264px] h-auto px-6 py-3 bg-sidebar_white_background border border-t-0 border-b-0">
        <div className="border-b-2 pb-3">
          {user && (
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-2">
                <div className="w-[30px] h-[30px] flex items-center justify-center">
                  <UserButton />
                </div>
                <div className="flex flex-col">
                  <span className="text-sm font-medium">{user.fullName}</span>
                  <span className="text-xs">{user.primaryEmailAddress?.emailAddress}</span>
                </div>
              </div>
            </div>
          )}
        </div>
        <div className="">
          <SignOutButton>
          <div className="flex gap-2 items-center justify-center pt-1 cursor-pointer">
                <span className="text-[10px] text-sidebar_bottom_red_logout_text_color font-medium">Log Out</span>
                <span className="cursor-pointer">
                  <LogoutIcon />
                </span>
          </div>
          </SignOutButton> 
        </div>
      </div>
    </>
  );
};

export default Navbar;
